const formatBotMessage = (message) => {
  // Mengubah blok kode menjadi tampilan dengan gaya khusus
  let formattedMessage = message.replace(/```([\s\S]*?)```/g, `<pre class="code-block bg-gray-900 text-white p-4 rounded-lg overflow-x-auto"><code>$1</code></pre>`);

  // Bold
  formattedMessage = formattedMessage.replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>");
  // Bullet points
  formattedMessage = formattedMessage.replace(/^\* (.*?)(?=\n|\r|$)/gm, "<ul><li>$1</li></ul>");
  // Line breaks
  formattedMessage = formattedMessage.replace(/\n/g, "<br>");

  return formattedMessage;
};

const App = () => {
  const [messages, setMessages] = useState(
    () =>
      JSON.parse(localStorage.getItem("chatHistory")) || [
        {
          role: "model",
          content: formatBotMessage("Great to meet you. What would you like to know?"),
        },
      ]
  );
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  useEffect(() => {
    scrollToBottom();
    localStorage.setItem("chatHistory", JSON.stringify(messages));
  }, [messages]);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const userMessage = { role: "user", content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");
    setLoading(true);

    try {
      const result = await chat.sendMessage(input);
      const formattedResponse = formatBotMessage(result.response.text());
      const botMessage = { role: "model", content: formattedResponse };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Error generating response: ", error);
      setMessages((prevMessages) => [...prevMessages, { role: "model", content: formatBotMessage("Something went wrong. Please try again.") }]);
    } finally {
      setLoading(false);
    }
  };

  const handleNewChat = () => {
    setMessages([{ role: "model", content: formatBotMessage("Great to meet you. What would you like to know?") }]);
    localStorage.removeItem("chatHistory");
  };

  return (
    <div className="flex flex-col items-center h-screen bg-black p-4">
      <div className="w-full max-w-4xl bg-black flex-1 p-4 overflow-y-auto">
        <div className="space-y-4">
          {messages.map((message, index) => (
            <div key={index} className={`flex ${message.role === "user" ? "justify-end" : "justify-start"}`}>
              <div
                className={`p-4 max-w-3xl ${message.role === "user" ? "bg-black text-white rounded-l-3xl rounded-tr-3xl" : "bg-gray-200 text-black rounded-r-3xl rounded-tl-3xl"}`}
                dangerouslySetInnerHTML={{ __html: message.content }}
              ></div>
            </div>
          ))}
          {loading && (
            <div className="flex justify-start">
              <div className="bg-white p-3 rounded-l-lg flex items-center">
                <div className="flex space-x-1">
                  <span className="w-2 h-2 bg-gray-500 rounded-full animate-pulse"></span>
                  <span className="w-2 h-2 bg-gray-500 rounded-full animate-pulse delay-200"></span>
                  <span className="w-2 h-2 bg-gray-500 rounded-full animate-pulse delay-400"></span>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef}></div>
        </div>
      </div>
      <div className="w-full max-w-4xl bg-gray-800 p-3 flex items-center rounded-l-lg">
        <input
          type="text"
          placeholder="Write a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && !loading && handleSend()}
          className="flex-1 p-2 bg-gray-900 rounded-full text-white focus:outline-none focus:ring focus:ring-gray-600"
        />
        <button onClick={handleSend} disabled={loading} className="ml-2 p-2 bg-gray-700 text-white rounded-lg hover:bg-gray-900 disabled:bg-gray-800">
          Send
        </button>
      </div>
      <div className="absolute top-4 right-4">
        <button onClick={handleNewChat} className="p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-500">
          New Chat
        </button>
      </div>
    </div>
  );
};

export default App;
