import React, { useState } from "react";
import { GoogleGenerativeAI } from "@google/generative-ai";

// Pastikan API Key sudah ada di .env file
const genAI = new GoogleGenerativeAI("AIzaSyBRoLDddSyrDtvscgXCThgmun6_Un4nuxM");
const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
const chat = model.startChat({
  history: [
    {
      role: "user",
      parts: [{ text: "Hello" }],
    },
    {
      role: "model",
      parts: [{ text: "Great to meet you. What would you like to know?" }],
    },
  ],
});

const App = () => {
  const [messages, setMessages] = useState([{ role: "model", content: "Great to meet you. What would you like to know?" }]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const userMessage = { role: "user", content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");
    setLoading(true);

    try {
      // Mengirim pesan ke Gemini API
      const result = await chat.sendMessage(input);
      const botMessage = { role: "model", content: result.response.text() };

      // Update state dengan pesan baru dari model
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Error generating response: ", error);
      setMessages((prevMessages) => [...prevMessages, { role: "model", content: "Something went wrong. Please try again." }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col mx-80 h-screen bg-gray-50 border border-gray-300 ">
      {/* Chat Window */}
      <div className="flex-1 border border-red-400 overflow-y-auto p-4">
        {messages.map((msg, index) => (
          <div key={index} className={`my-2 p-2 border border-blue-400 rounded-lg max-w-xs  ${msg.role === "user" ? " bg-yellow-800 text-white self-end" : "bg-gray-300 text-black self-start "}`}>
            {msg.content}
          </div>
        ))}
      </div>

      {/* Input Section */}
      <div className="flex items-center p-4 border-t bg-white">
        <input type="text" value={input} onChange={(e) => setInput(e.target.value)} placeholder="Type your message..." className="flex-1 p-2 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-blue-400" />
        <button onClick={handleSend} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600" disabled={loading}>
          {loading ? "Sending..." : "Send"}
        </button>
      </div>
    </div>
  );
};

export default App;
