import React, { useState } from "react";
import { GoogleGenerativeAI } from "@google/generative-ai";

// Pastikan API Key sudah ada di .env file
const genAI = new GoogleGenerativeAI("AIzaSyBRoLDddSyrDtvscgXCThgmun6_Un4nuxM");
const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
const chat = model.startChat({
  history: [
    {
      role: "user",
      parts: [{ text: "Hello" }],
    },
    {
      role: "model",
      parts: [{ text: "Great to meet you. What would you like to know?" }],
    },
  ],
});

const App = () => {
  const [messages, setMessages] = useState([{ role: "model", content: "Great to meet you. What would you like to know?" }]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const userMessage = { role: "user", content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");
    setLoading(true);

    try {
      // Mengirim pesan ke Gemini API
      const result = await chat.sendMessage(input);
      const botMessage = { role: "model", content: result.response.text() };

      // Update state dengan pesan baru dari model
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Error generating response: ", error);
      setMessages((prevMessages) => [...prevMessages, { role: "model", content: "Something went wrong. Please try again." }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center h-screen bg-gray-100 p-4">
      {/* Chat Header */}
      <div className="w-full max-w-md bg-purple-600 text-white p-3 rounded-t-lg">
        <h1 className="text-center font-bold text-lg">Chatbot</h1>
      </div>

      {/* Chat Body */}
      <div className="w-full max-w-md bg-white flex-1 p-4 overflow-y-auto rounded-b-lg shadow">
        {/* Chat Messages */}
        <div className="space-y-4">
          {/* Chatbot message */}
          <div className="flex items-start space-x-2">
            <div className="bg-purple-600 p-2 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="w-5 h-5 text-white">
                <path strokeLinecap="round" strokeLinejoin="round" d="M12 20.25c4.97 0 9-4.03 9-9s-4.03-9-9-9-9 4.03-9 9 4.03 9 9 9z" />
              </svg>
            </div>
            <div className="bg-gray-100 p-3 rounded-lg">
              <p>Hi there ðŸ‘‹</p>
              <p>How can I help you today?</p>
            </div>
          </div>

          {/* User message */}
          <div className="flex items-end justify-end space-x-2">
            <div className="bg-purple-600 text-white p-3 rounded-lg max-w-xs">I'm interested in purchasing the laptop.</div>
          </div>

          {/* Chatbot response */}
          <div className="flex items-start space-x-2">
            <div className="bg-purple-600 p-2 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="w-5 h-5 text-white">
                <path strokeLinecap="round" strokeLinejoin="round" d="M12 20.25c4.97 0 9-4.03 9-9s-4.03-9-9-9-9 4.03-9 9 4.03 9 9 9z" />
              </svg>
            </div>
            <div className="bg-gray-100 p-3 rounded-lg">
              <p>What is your budget for the laptop?</p>
            </div>
          </div>

          {/* User response */}
          <div className="flex items-end justify-end space-x-2">
            <div className="bg-purple-600 text-white p-3 rounded-lg max-w-xs">I have a budget of $1000.</div>
          </div>

          {/* Chatbot recommendation */}
          <div className="flex items-start space-x-2">
            <div className="bg-purple-600 p-2 rounded-full">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="w-5 h-5 text-white">
                <path strokeLinecap="round" strokeLinejoin="round" d="M12 20.25c4.97 0 9-4.03 9-9s-4.03-9-9-9-9 4.03-9 9 4.03 9 9 9z" />
              </svg>
            </div>
            <div className="bg-gray-100 p-3 rounded-lg">
              <p>I recommend considering an "ABC Laptop" model.</p>
            </div>
          </div>
        </div>
      </div>

      {/* Chat Input */}
      <div className="w-full max-w-md bg-white p-3 border-t flex items-center rounded-b-lg shadow">
        <input type="text" placeholder="Write a message..." className="flex-1 p-2 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-purple-400" />
        <button className="ml-2 p-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="2" stroke="currentColor" className="w-5 h-5">
            <path strokeLinecap="round" strokeLinejoin="round" d="M14.25 19.5L21 12m0 0l-6.75-7.5M21 12H3" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default App;
