import React, { useState } from "react";

const App = () => {
  const [messages, setMessages] = useState([{ role: "system", content: "You are a helpful assistant." }]);
  const [input, setInput] = useState("");

  const handleSend = async () => {
    if (input.trim() === "") return;

    const userMessage = { role: "user", content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");

    try {
      const response = await fetch("https://api.openai.com/v1/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer 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`,
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo-instruct",
          messages: updatedMessages,
          max_tokens: 7,
          temperature: 0,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const botMessage = data.choices[0].message;

      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      console.error("Error fetching response:", error);
      alert("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      {/* Chat Window */}
      <div className="flex-1 overflow-y-auto p-4">
        {messages.map((msg, index) => (
          <div key={index} className={`my-2 p-2 rounded-lg max-w-xs ${msg.role === "user" ? "bg-blue-500 text-white self-end" : "bg-gray-300 text-black self-start"}`}>
            {msg.content}
          </div>
        ))}
      </div>

      {/* Input Section */}
      <div className="flex items-center p-4 border-t bg-white">
        <input type="text" value={input} onChange={(e) => setInput(e.target.value)} placeholder="Type your message..." className="flex-1 p-2 border rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-blue-400" />
        <button onClick={handleSend} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
          Send
        </button>
      </div>
    </div>
  );
};

export default App;
